import Constants from "../../constants";
import Token from "./Token";
import { ClockIcon, WifiIcon, CalendarIcon } from '@heroicons/react/solid'


const IconedText = ({icon, children, color}) => (
    <span>
        <icon className={"h-6 mx-1 w-6 inline text-" + color}/>
        {children}
    </span>
)


export default function Notification(props) {
    const sever = props.data.severity;
    const color = sever === Constants.SEVERITY_HIGH ? "red-500" :
                    sever === Constants.SEVERITY_MID ? "orange-400" : "emerald-500";
    return (
        <div className="notification-cont rounded-md p-1 bg-white drop-shadow">
            <div className={"rounded-md p-3 bg-white border-" + color + " border-2"}>
                {/* words */}
                <div className="gap-1">
                    {props.data.words.map(word => <Token color={color}>{word}</Token>)}
                </div>
                {/* data */}
                <div className="grid grid-cols-2 grid-rows-2">
                    <span>
                        <ClockIcon className={"h-6 w-6 mx-1 inline text-" + color}/>
                        {props.data.time}
                    </span>
                    <span>
                        <WifiIcon className={"h-6 mx-1 w-6 inline text-" + color}/>
                        {props.data.freq}
                    </span>
                    CalendarIcon
                </div>
            </div>
        </div>
    )
}
