import { Component } from 'react';
import Notification from "../stateless/Notification";
import Popup from "../stateless/Popup";
import IconedText from "../stateless/IconedText";
import WordTokenList from "../stateless/WordTokenList";
import { ClockIcon, WifiIcon, CalendarIcon, LocationMarkerIcon, CheckCircleIcon, EyeIcon } from '@heroicons/react/solid'
import AudioPlayer from 'react-h5-audio-player';
import 'react-h5-audio-player/lib/styles.css';


class NotificationsBody extends Component {
    constructor(props) {
        super(props);
        this.state = {
            viewedNotId: undefined
        };
    }

    onView(notId) {
        this.setState({viewedNotId: notId}, () => this.props.setPopupOpen(true));
    }

    onViewClose() {
        this.setState({viewedNotId: undefined}, () => this.props.setPopupOpen(false));
    }

    render() {
        const viewedNot = this.state.viewedNotId &&
                            this.props.notifications.find(n => n.id === this.state.viewedNotId);
        return (
            <div>
                {
                    viewedNot && (
                        <Popup onClose={this.onViewClose.bind(this)} >
                            {/* Metadate */}
                            <span className="text-center">
                                <WordTokenList wordIds={viewedNot.words} words={this.props.words} />

                                <div className="grid gap-2 grid-cols-[1fr_auto_auto_auto_auto_1fr]">
                                    <span></span>
                                    <IconedText icon={ClockIcon} color={''}>{viewedNot.time}</IconedText>
                                    <IconedText icon={WifiIcon} color={''}>{viewedNot.freq}</IconedText>
                                    <IconedText icon={CalendarIcon} color={''}>{viewedNot.date}</IconedText>
                                    <IconedText icon={LocationMarkerIcon} color={''}>{viewedNot.location}</IconedText>
                                </div>
                            </span>

                            {/* The STT text */}
                            <div className="my-6">
                                <h3 className="text-gray-500 text-lg">תמלול השיחה</h3>
                            </div>

                            {/* Audio player */}
                            <span style={{direction: "ltr"}}>
                                <AudioPlayer
                                    customAdditionalControls={[]}
                                    src={viewedNot.audio}
                                />
                            </span>
                        </Popup>
                    )
                }

                <div className={"grid grid-cols-4 gap-8 p-5 " + (this.state.viewedNotId !== undefined && " blur")}>
                    {/* TODO: filtering, sorting, etc... */}
                    {this.props.notifications.map(not => (
                        <Notification data={not} markNotificationAsRead={this.props.markNotificationAsRead}
                                    words={this.props.words} key={not.id} onView={this.onView.bind(this)}/>
                    ))}
                </div>
            </div>
        );
    }
}


export default NotificationsBody;
