import { Component } from 'react';
import './App.css';
import Menu from "./components/stateless/Menu"; 


const PAGE_NOTF = "notifications";
const PAGE_WORDS = "words";
const SEVERITY_HIGH = 1
const SEVERITY_MID = 2
const SEVERITY_LOW = 3


class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      page: PAGE_NOTF,
      notifications: [
        {
          severity: SEVERITY_HIGH,
          words: ["הטעיה", "תירס חם"],
          freq: 52.5,
          time: "11:24"
        },
        {
          severity: SEVERITY_MID,
          words: ["מנהרה"],
          freq: 52.5,
          time: "13:51"
        }
      ]
    }
  }

  render() {
    return (
      <div className="App">
        <Menu page={this.state.page}/>
      </div>
    );
  }
}

export default {App, PAGE_NOTF, PAGE_WORDS};
