import { Component } from 'react';
import Token from "../stateless/Token";
import { XIcon, PencilIcon } from '@heroicons/react/solid'
import Constants from "../../constants";


function Word({word}) {
    const color = Constants.SEVERITY_TO_COLOR[word.severity];
    return (
        <span className="px-2 text-xl select-none pl-3">
            <Token color={color} customStyles="text-base">
                <XIcon className={"h-4 pb-0.5 cursor-pointer mx-1 w-4 text-white inline"}/>
                {word.word}
                {/* For now don't allow editing 
                <PencilIcon className={"h-4 pb-0.5 cursor-pointer mx-1 w-4 text-white inline"}/> */}
            </Token>
        </span>
    )
}


class WordsBody extends Component {
    constructor(props) {
        super(props);
        this.state = {
            editedWordId: undefined
        };
    }

    render() {
        return (
            <div className="p-5">
                {/* TODO: filtering, sorting, etc... */}
                {this.props.words.map(w => (
                    <Word word={w} key={w.id}/>
                ))}
            </div>
        );
    }
}


export default WordsBody;
